dataSet.query:
SELECT Products.Ref AS Products, Products.ProductsCategory AS ProductsKind, VALUE(Catalog.ProductsCharacteristics.EmptyRef) AS Characteristic, VALUE(Catalog.ProductsBatches.EmptyRef) AS Batch, VALUE(Catalog.UOM.EmptyRef) AS MeasurementUnit INTO ProductsAndCharacteristics FROM Catalog.Products AS Products WHERE Not Products.IsFolder //AND Products.UseCharacteristics = FALSE {WHERE Products.Ref.* AS Products, (VALUE(Catalog.ProductsCharacteristics.EmptyRef)) AS Characteristic, (VALUE(Catalog.ProductsBatches.EmptyRef)) AS Batch, (VALUE(Catalog.UOM.EmptyRef)) AS MeasurementUnit}
UNION ALL
SELECT Products.Ref, Products.ProductsCategory, ProductsCharacteristics.Ref, VALUE(Catalog.ProductsBatches.EmptyRef), VALUE(Catalog.UOM.EmptyRef) FROM Catalog.Products AS Products INNER JOIN Catalog.ProductsCharacteristics AS ProductsCharacteristics ON Products.Ref = ProductsCharacteristics.Owner //WHERE // Products.UseCharacteristics = TRUE {WHERE Products.Ref.* AS Products, ProductsCharacteristics.Ref.* AS Characteristic, (VALUE(Catalog.ProductsBatches.EmptyRef)) AS Batch, (VALUE(Catalog.UOM.EmptyRef)) AS MeasurementUnit} ;
//////////////////////////////////////////////////////////////////////////////// SELECT ProductsAndCharacteristics.Products AS Products, ProductsAndCharacteristics.Characteristic AS Characteristic, ProductsAndCharacteristics.Batch AS Batch, ProductsAndCharacteristics.MeasurementUnit AS MeasurementUnit INTO ProductsAndCharacteristicsAndBatches FROM ProductsAndCharacteristics AS ProductsAndCharacteristics
UNION ALL
SELECT ProductsAndCharacteristics.Products, ProductsAndCharacteristics.Characteristic, ProductsBatches.Ref, ProductsAndCharacteristics.MeasurementUnit FROM Catalog.ProductsBatches AS ProductsBatches INNER JOIN ProductsAndCharacteristics AS ProductsAndCharacteristics ON ProductsBatches.Owner = ProductsAndCharacteristics.Products {WHERE ProductsAndCharacteristics.Products.* AS Products, ProductsAndCharacteristics.Characteristic.* AS Characteristic, ProductsBatches.Ref.* AS Batch, ProductsAndCharacteristics.MeasurementUnit.* AS MeasurementUnit} ;
//////////////////////////////////////////////////////////////////////////////// SELECT ProductsAndCharacteristicsAndBatches.Products AS Products, ProductsAndCharacteristicsAndBatches.Characteristic AS Characteristic, ProductsAndCharacteristicsAndBatches.Batch AS Batch, ProductsAndCharacteristicsAndBatches.MeasurementUnit AS MeasurementUnit INTO SourceData FROM ProductsAndCharacteristicsAndBatches AS ProductsAndCharacteristicsAndBatches
UNION ALL
SELECT ProductsAndCharacteristicsAndBatches.Products, ProductsAndCharacteristicsAndBatches.Characteristic, ProductsAndCharacteristicsAndBatches.Batch, UnitDimensionsProducts.Ref FROM Catalog.UOM AS UnitDimensionsProducts INNER JOIN ProductsAndCharacteristicsAndBatches AS ProductsAndCharacteristicsAndBatches ON UnitDimensionsProducts.Owner = ProductsAndCharacteristicsAndBatches.Products {WHERE ProductsAndCharacteristicsAndBatches.Products.* AS Products, ProductsAndCharacteristicsAndBatches.Characteristic.* AS Characteristic, ProductsAndCharacteristicsAndBatches.Batch.* AS Batch, UnitDimensionsProducts.Ref.* AS MeasurementUnit}
INDEX BY Products, Characteristic, Batch, MeasurementUnit ;
//////////////////////////////////////////////////////////////////////////////// SELECT DISTINCT SourceDataLastQuery.Products AS Products, SourceDataLastQuery.Characteristic AS Characteristic, SourceDataLastQuery.Batch AS Batch, SourceDataLastQuery.MeasurementUnit AS MeasurementUnit, ISNULL(ProductsCodesPeripheralOffline.Code, 0) AS Code, ISNULL(ProductsCodesPeripheralOffline.Used, FALSE) AS Used INTO DataTable {SELECT Products.*, Characteristic.*, Batch.*, Code, Used, MeasurementUnit.*} FROM SourceData AS SourceDataLastQuery {LEFT JOIN InformationRegister.Barcodes AS Barcodes ON SourceDataLastQuery.Products = Barcodes.Products AND SourceDataLastQuery.Characteristic = Barcodes.Characteristic AND SourceDataLastQuery.Batch = Barcodes.Batch AND SourceDataLastQuery.MeasurementUnit = Barcodes.MeasurementUnit} {LEFT JOIN InformationRegister.Prices.SliceLast(ENDOFPERIOD(&Date, Day), PriceKind = &PriceKind {(Products).* AS Products, (Characteristic).* AS Characteristic}) AS PricesSliceLast ON SourceDataLastQuery.Products = PricesSliceLast.Products AND SourceDataLastQuery.Characteristic = PricesSliceLast.Characteristic} {LEFT JOIN AccumulationRegister.InventoryInWarehouses.Balance(, {(Products).* AS Products, (Characteristic).* AS Characteristic, (Batch).* AS Batch, (StructuralUnit).* AS StructuralUnit}) AS ProductsInInventoryBalances ON SourceDataLastQuery.Products = ProductsInInventoryBalances.Products AND SourceDataLastQuery.Characteristic = ProductsInInventoryBalances.Characteristic AND SourceDataLastQuery.Batch = ProductsInInventoryBalances.Batch} {LEFT JOIN InformationRegister.ProductsCodesPeripheralOffline AS ProductsCodesPeripheralOffline ON SourceDataLastQuery.Products = ProductsCodesPeripheralOffline.Products AND SourceDataLastQuery.Characteristic = ProductsCodesPeripheralOffline.Characteristic AND SourceDataLastQuery.Batch = ProductsCodesPeripheralOffline.Batch AND SourceDataLastQuery.MeasurementUnit = ProductsCodesPeripheralOffline.MeasurementUnit AND (ProductsCodesPeripheralOffline.ExchangeRule = &ExchangeRule)} {WHERE SourceDataLastQuery.Products.* AS Products, SourceDataLastQuery.Characteristic.* AS Characteristic, SourceDataLastQuery.Batch.* AS Batch, SourceDataLastQuery.MeasurementUnit.* AS MeasurementUnit, Barcodes.Barcode AS Barcode, PricesSliceLast.Price AS Price, ProductsInInventoryBalances.QuantityBalance AS BalanceAtWarehouse} ;
//////////////////////////////////////////////////////////////////////////////// SELECT TRUE AS MatchesSelection, DataTable.Products AS Products, DataTable.Characteristic AS Characteristic, DataTable.Batch AS Batch, DataTable.MeasurementUnit AS MeasurementUnit, DataTable.Code AS Code, DataTable.Used AS Used {SELECT MatchesSelection, Code, Used, Products.*, Characteristic.*, Batch.*, MeasurementUnit.*} FROM DataTable AS DataTable WHERE (DataTable.Code = 0 OR Not DataTable.Used)
UNION ALL
SELECT FALSE, ProductsCodesPeripheralOffline.Products, ProductsCodesPeripheralOffline.Characteristic, ProductsCodesPeripheralOffline.Batch, ProductsCodesPeripheralOffline.MeasurementUnit, ProductsCodesPeripheralOffline.Code, ProductsCodesPeripheralOffline.Used FROM InformationRegister.ProductsCodesPeripheralOffline AS ProductsCodesPeripheralOffline WHERE Not (ProductsCodesPeripheralOffline.Products, ProductsCodesPeripheralOffline.Characteristic, ProductsCodesPeripheralOffline.Batch, ProductsCodesPeripheralOffline.MeasurementUnit) In (SELECT T.Products, T.Characteristic, T.Batch, T.MeasurementUnit FROM DataTable AS T) AND ProductsCodesPeripheralOffline.Products <> VALUE(Catalog.Products.EmptyRef) AND ProductsCodesPeripheralOffline.ExchangeRule = &ExchangeRule AND ProductsCodesPeripheralOffline.Used
ORDER BY Code
